import React, { useEffect } from 'react';
import { useRoutes, useNavigate } from 'react-router-dom';
import HomePage from './pages/HomePage';  // Example Home page
import Auth from './pages/Auth';  // Example Login/Signup page
import FileShare from './pages/FileShare';  // Example FileShare page

const App = () => {
  const navigate = useNavigate();

  // Check for the token in localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');

    // If token exists, redirect to HomePage, else go to Login page
    if (token) {
      navigate('/home');
    } else {
      navigate('/');
    }
  }, [navigate]);

  // Define the routes
  const routes = [
    { path: '/', element: <Auth /> },  // Login page
    { path: '/home', element: <HomePage /> },  // Home page
    { path: '/file-share', element: <FileShare /> },  // FileShare page
    // Add more routes as needed
  ];

  const element = useRoutes(routes);

  return element;  // Return the routes element
};

export default App;
