import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './FileShare.css';

const socket = io('http://localhost:5000'); // Server URL

const FileShare = () => {
  const [file, setFile] = useState(null);
  const [peerConnection, setPeerConnection] = useState(new RTCPeerConnection());
  const [dataChannel, setDataChannel] = useState(null);
  const [status, setStatus] = useState('');
  const [isOfferSent, setIsOfferSent] = useState(false);
  const [receivedFileChunks, setReceivedFileChunks] = useState([]); // Collects chunks
  const CHUNK_SIZE = 16384;

  useEffect(() => {
    console.log("Setting up WebRTC peer connection and data channel...");

    const channel = peerConnection.createDataChannel('fileTransfer');
    setDataChannel(channel);

    channel.onopen = () => {
      console.log('Data channel opened');
      setStatus('Data channel is open.');
    };

    channel.onclose = () => {
      console.log('Data channel closed');
      setStatus('Data channel is closed.');
    };

    channel.onmessage = (event) => {
      console.log("Receiving data chunk on data channel...");
      if (event.data instanceof Blob) {
        const reader = new FileReader();
        reader.onload = () => {
          const receivedChunk = reader.result;
          setReceivedFile((prevFile) => {
            if (!prevFile) {
              return receivedChunk;
            } else {
              return new Blob([prevFile, receivedChunk], { type: 'application/octet-stream' });
            }
          });
          console.log("Chunk received and stored:", receivedChunk.byteLength);
        };
        reader.readAsArrayBuffer(event.data);
      } else {
        console.warn("Unexpected data type:", event.data);
      }
    };

    // Socket event listeners for signaling
    socket.on('offer', (offer) => {
      console.log("Received offer:", offer);
      peerConnection.setRemoteDescription(new RTCSessionDescription(offer))
        .then(() => peerConnection.createAnswer())
        .then(answer => {
          peerConnection.setLocalDescription(answer);
          socket.emit('answer', answer);
          console.log("Answer sent to the offer.");
        })
        .catch(err => console.error("Error handling offer:", err));
    });

    socket.on('answer', (answer) => {
      console.log("Received answer:", answer);
      peerConnection.setRemoteDescription(new RTCSessionDescription(answer))
        .catch(err => console.error("Error setting remote description:", err));
    });

    socket.on('ice-candidate', (candidate) => {
      console.log("Received ICE candidate:", candidate);
      peerConnection.addIceCandidate(new RTCIceCandidate(candidate))
        .catch(err => console.error("Error adding ICE candidate:", err));
    });

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', event.candidate);
        console.log("ICE candidate sent:", event.candidate);
      }
    };

    return () => {
      socket.off('offer');
      socket.off('answer');
      socket.off('ice-candidate');
    };
  }, [peerConnection]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setStatus('');
    console.log("File selected:", event.target.files[0]);
  };

  const sendFile = () => {
    if (!file || !dataChannel) {
      setStatus('No file selected or data channel not available.');
      console.warn('No file selected or data channel is not available.');
      return;
    }

    if (dataChannel.readyState !== 'open') {
      setStatus('Data channel is not open yet.');
      console.warn("Data channel is not open. Current state:", dataChannel.readyState);
      return;
    }

    const fileReader = new FileReader();
    let offset = 0;

    fileReader.onload = (event) => {
      dataChannel.send(event.target.result);
      offset += event.target.result.byteLength;
      console.log(`Sent chunk: ${offset}/${file.size}`);

      if (offset < file.size) {
        sendNextChunk();
      } else {
        setStatus('File sent successfully!');
        setFile(null);
        console.log("File sent completely.");
      }
    };

    const sendNextChunk = () => {
      const chunk = file.slice(offset, offset + CHUNK_SIZE);
      fileReader.readAsArrayBuffer(chunk);
      dataChannel.send(chunk);
    };

    sendNextChunk();
  };

  

  const createOffer = () => {
    if (isOfferSent) return;

    peerConnection.createOffer()
      .then((offer) => {
        peerConnection.setLocalDescription(offer);
        socket.emit('offer', offer);
        setIsOfferSent(true);
        setStatus('Offer sent!');
        console.log("Offer created and sent.");
      })
      .catch(err => {
        console.error('Error creating offer:', err);
        setStatus('Error creating offer.');
      });
  };

  const handleReceiveFile = () => {
    if (!receivedFile) {
      setStatus('No file available to receive.');
      console.warn('No file to receive.');
      return;
    }

    const link = document.createElement('a');
    link.href = URL.createObjectURL(receivedFile);
    link.download = 'received_file';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setStatus('File received successfully!');
    setReceivedFile(null);
    console.log("File downloaded successfully.");
  };

    const receivedBlob = new Blob(receivedFileChunks);
    const link = document.createElement('a');
    link.href = URL.createObjectURL(receivedBlob);
    link.download = 'received_file';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setStatus('File received successfully!');
    setReceivedFileChunks([]);
    console.log("File downloaded successfully.");
  

  return (
    <div className="file-share-container">
      <h1>File Share App</h1>
      <div className="file-input">
        <input type="file" onChange={handleFileChange} />
        <button onClick={sendFile}>Send File</button>
        <button onClick={createOffer}>Create Offer</button>
        <button onClick={handleReceiveFile}>Receive File</button>
      </div>
      {status && <p className="status-message">{status}</p>}
    </div>
  );
};

export default FileShare;
