import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Header from '../components/Header';
import Modal from '../components/Modal';
import { socket } from '../socket'; // Assuming you have a socket.js file

export default function HomePage() {
  const navigate = useNavigate();
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal for logout
  const [requests, setRequests] = useState([]); // Incoming connection requests
  const [status, setStatus] = useState(''); // Connection status messages
  const [messageCount, setMessageCount] = useState(0); // Track unread requests

  // Handle logout button click
  const handleLogoutClick = () => {
    setIsModalOpen(true); // Show the modal on logout click
  };

  // Close the modal
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Confirm logout and redirect to login
  const handleLogoutConfirm = () => {
    localStorage.removeItem('token'); // Remove token from localStorage
    socket.emit('logout'); // Notify server about the logout
    socket.emit('logout', { user: localStorage.getItem('username') }); // Disconnect the socket
    socket.disconnect();
    setIsModalOpen(false); // Close the modal
    navigate('/'); // Redirect to login page
  };

  // Handle Accept connection request
  const handleAcceptRequest = (fromUsername) => {
    socket.emit('connection-response', { toUsername: fromUsername, accepted: true });
    setRequests((prevRequests) => prevRequests.filter((username) => username !== fromUsername));
    setMessageCount((prevCount) => prevCount - 1);
  };

  // Handle Decline connection request
  const handleDeclineRequest = (fromUsername) => {
    socket.emit('connection-response', { toUsername: fromUsername, accepted: false });
    setRequests((prevRequests) => prevRequests.filter((username) => username !== fromUsername));
    setMessageCount((prevCount) => prevCount - 1);
  };

  useEffect(() => {
    // Listen for incoming connection requests
    socket.on('connection-request', ({ fromUsername }) => {
      setRequests((prevRequests) => [...prevRequests, fromUsername]);
      setMessageCount((prevCount) => prevCount + 1);
    });

    // Listen for connection response (denied or accepted)
    socket.on('connection-denied', ({ message }) => {
      setStatus(message); // Display denial message
    });

    socket.on('connection-approved', () => {
      setStatus('Connection approved!');
    });

    return () => {
      socket.off('connection-request');
      socket.off('connection-denied');
      socket.off('connection-approved');
    };
  }, []);

  return (
    <div>
      {/* Header with message count */}
      <Header messageCount={messageCount} />

      <main>
        {/* Home Section */}
        <section id="home">
          <h2>Welcome to Accio</h2>
          <p>This is the home page of our sample application.</p>

          {/* FileShare link */}
          <Link to="/file-share">
            <button>Go to FileShare</button>
          </Link>

          {/* Log out button */}
          <button onClick={handleLogoutClick}>Log Out</button>
        </section>

        {/* About Section */}
        <section id="about">
          <h2>About Us</h2>
          <p>Learn more about our services and mission.</p>
        </section>

        {/* Services Section */}
        <section id="services">
          <h2>Our Services</h2>
          <p>We offer a range of services to meet your needs.</p>
        </section>

        {/* Contact Section */}
        <section id="contact">
          <h2>Contact Us</h2>
          <p>Feel free to reach out for more information!</p>
        </section>
      </main>

      {/* Modal for logout confirmation */}
      <Modal 
        isOpen={isModalOpen}
        onClose={closeModal}
        onConfirm={handleLogoutConfirm}
      />

      {/* Status Display */}
      {status && <p className="status">{status}</p>}

      {/* Connection Requests Section */}
      {requests.length > 0 && (
        <div className="requests-list">
          <h3>Connection Requests</h3>
          <ul>
            {requests.map((username) => (
              <li key={username}>
                {username} 
                <button onClick={() => handleAcceptRequest(username)}>Accept</button>
                <button onClick={() => handleDeclineRequest(username)}>Decline</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
