import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import download from "downloadjs";
//import "./styles.css";

const Receiver = () => {
  const [joinID, setJoinID] = useState("");
  const [senderID, setSenderID] = useState("");
  const [fileShare, setFileShare] = useState(null);
  const [sharedFiles, setSharedFiles] = useState([]);
  const [activeScreen, setActiveScreen] = useState("join-screen");

  const socket = io("http://localhost:5000");

  const generateID = () => {
    return `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
  };

  const [receivedFiles, setReceivedFiles] = useState([]);

useEffect(() => {
  socket.on("file-raw", (data) => {
    const { buffer, uid } = data;
    let file = receivedFiles.find((file) => file.uid === uid);

    if (!file) {
      file = {
        uid,
        buffer: [],
        progress: 0,
      };
      setReceivedFiles((prev) => [...prev, file]);
    }

    // Append the incoming chunk to the buffer
    file.buffer.push(new Uint8Array(buffer));

    const totalBufferSize = file.buffer.reduce((acc, chunk) => acc + chunk.length, 0);
    const progress = Math.trunc((totalBufferSize / file.metadata.total_buffer_size) * 100);

    file.progress = progress;
    setReceivedFiles((prev) => [...prev]);

    // If the entire file has been received
    if (totalBufferSize === file.metadata.total_buffer_size) {
      const completeBuffer = file.buffer.reduce((acc, chunk) => {
        const temp = new Uint8Array(acc.length + chunk.length);
        temp.set(acc);
        temp.set(chunk, acc.length);
        return temp;
      }, new Uint8Array(0));

      // Convert the buffer back to a Blob and create a download link
      const blob = new Blob([completeBuffer]);
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = file.metadata.filename;
      link.click();

      // Optionally, remove the file from the list once the download is complete
      setReceivedFiles((prev) => prev.filter((f) => f.uid !== file.uid));
    }
  });

  return () => {
    socket.off("file-raw");
  };
}, [receivedFiles]);


  useEffect(() => {
    socket.on("fs-meta", (metadata) => {
      const newFileShare = {
        metadata,
        transmitted: 0,
        buffer: [],
        progress: 0,
      };

      setFileShare(newFileShare);
      setSharedFiles((prev) => [
        ...prev,
        { filename: metadata.filename, progress: 0 },
      ]);

      socket.emit("fs-start", { uid: senderID });
    });

    socket.on("fs-share", (buffer) => {
      setFileShare((prev) => {
        if (!prev) return null;

        const updatedFileShare = { ...prev };
        updatedFileShare.buffer.push(buffer);
        updatedFileShare.transmitted += buffer.byteLength;
        updatedFileShare.progress = Math.trunc(
          (updatedFileShare.transmitted /
            updatedFileShare.metadata.total_buffer_size) *
            100
        );

        const updatedFiles = sharedFiles.map((file) =>
          file.filename === updatedFileShare.metadata.filename
            ? { ...file, progress: updatedFileShare.progress }
            : file
        );
        setSharedFiles(updatedFiles);

        if (
          updatedFileShare.transmitted ===
          updatedFileShare.metadata.total_buffer_size
        ) {
          download(
            new Blob(updatedFileShare.buffer),
            updatedFileShare.metadata.filename
          );
          return null; // Reset fileShare
        } else {
          socket.emit("fs-start", { uid: senderID });
        }
        return updatedFileShare;
      });
    });

    return () => {
      socket.off("fs-meta");
      socket.off("fs-share");
    };
  }, [senderID, sharedFiles]);

  const handleConnect = () => {
    if (senderID.trim().length === 0) return;

    const id = generateID();
    setJoinID(id);

    socket.emit("receiver-join", {
      uid: id,
      sender_uid: senderID,
    });

    setActiveScreen("fs-screen");
  };

  return (
    <div className="app">
      {/* Join Screen */}
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <div className="form">
            <h2>Share your files securely</h2>
            <div className="form-input">
              <label htmlFor="join-id">Join ID</label>
              <input
                type="text"
                id="join-id"
                value={senderID}
                onChange={(e) => setSenderID(e.target.value)}
              />
            </div>
            <div className="form-input">
              <button id="receiver-start-con-btn" onClick={handleConnect}>
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* File Sharing Screen */}
      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <div className="files-list">
            <div className="title">Shared Files</div>
            {sharedFiles.map((file, index) => (
              <div key={index} className="item">
                <div className="progress">{file.progress}%</div>
                <div className="filename">{file.filename}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Receiver;
