import { io } from 'socket.io-client';

// Connect to the Socket.io server (use the correct URL for production)
const socket = io('http://localhost:5000'); // Use your server's URL here

socket.on('connect', () => {
    console.log('Connected to server:', socket.id);
  });

  socket.emit('connection-request', { toUsername, fromUsername });
console.log(`Sent connection request from ${fromUsername} to ${toUsername}`);

export { socket };
