import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function HomePage() {
  const navigate = useNavigate();

  // Handle logout
  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      localStorage.removeItem('token');  // Remove token from localStorage
      navigate('/');  // Redirect to login page
    }
  };

  return (
    <div>
      <Header />
      <main>
        <section id="home">
          <h2>Welcome to Accio</h2>
          <p>This is the home page of our sample application.</p>
          {/* Log out button */}
          <button onClick={handleLogout}>Log Out</button>
        </section>
        
        <section id="about">
          <h2>About Us</h2>
          <p>Learn more about our services and mission.</p>
        </section>
        
        <section id="services">
          <h2>Our Services</h2>
          <p>We offer a range of services to meet your needs.</p>
        </section>
        
        <section id="contact">
          <h2>Contact Us</h2>
          <p>Feel free to reach out for more information!</p>
        </section>
      </main>
    </div>
  );
}
