import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";
import download from "downloadjs";
//import "./styles.css";

const ReceiverApp = () => {
  const [senderID, setSenderID] = useState("");
  const [joinID, setJoinID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [fileShare, setFileShare] = useState({
    metadata: null,
    transmitted: 0,
    buffer: [],
    progressNode: null,
  });

  const socket = useRef(null);

  useEffect(() => {
    socket.current = io();

    socket.current.on("fs-meta", (metadata) => {
      setFileShare({
        metadata,
        transmitted: 0,
        buffer: [],
        progressNode: createProgressNode(metadata.filename),
      });

      socket.current.emit("fs-start", { uid: senderID });
    });

    socket.current.on("fs-share", (buffer) => {
      setFileShare((prev) => {
        const newTransmitted = prev.transmitted + buffer.byteLength;
        const progressPercentage = Math.trunc(
          (newTransmitted / prev.metadata.total_buffer_size) * 100
        );
        prev.progressNode.innerText = `${progressPercentage}%`;

        if (newTransmitted === prev.metadata.total_buffer_size) {
          download(
            new Blob([...prev.buffer, buffer]),
            prev.metadata.filename
          );
          return { metadata: null, transmitted: 0, buffer: [], progressNode: null };
        } else {
          socket.current.emit("fs-start", { uid: senderID });
          return {
            ...prev,
            transmitted: newTransmitted,
            buffer: [...prev.buffer, buffer],
          };
        }
      });
    });

    return () => {
      socket.current.disconnect();
    };
  }, [senderID]);

  const generateID = () => {
    return `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
  };

  const handleConnect = () => {
    if (senderID.trim().length === 0) return;
    const newJoinID = generateID();
    setJoinID(newJoinID);

    socket.current.emit("receiver-join", {
      uid: newJoinID,
      sender_uid: senderID,
    });

    setActiveScreen("fs-screen");
  };

  const createProgressNode = (filename) => {
    const el = document.createElement("div");
    el.classList.add("item");
    el.innerHTML = `
      <div class="progress">0%</div>
      <div class="filename">${filename}</div>
    `;
    document.querySelector(".files-list").appendChild(el);
    return el.querySelector(".progress");
  };

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <div className="form">
            <h2>Share your files securely</h2>
            <div className="form-input">
              <label htmlFor="join-id">Join ID</label>
              <input
                type="text"
                id="join-id"
                value={senderID}
                onChange={(e) => setSenderID(e.target.value)}
              />
            </div>
            <div className="form-input">
              <button id="receiver-start-con-btn" onClick={handleConnect}>
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <div className="files-list">
            <div className="title">Shared Files</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReceiverApp;
