import React from 'react';
import { Link } from 'react-router-dom';
import '../components/Header.css';

export default function Header({ messageCount = 0, notifications, onAccept, onDecline }) {
  return (
    <header>
      <h1>Accio</h1>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/user-prompt">FileShare</Link></li>
          <li><Link to="#about">About</Link></li>
          <li><Link to="#services">Services</Link></li>
          <li><Link to="#contact">Contact</Link></li>
        </ul>
        <div className="notifications">
          <Link to="/notifications">
            Notifications ({messageCount})
          </Link>
          <div className="notification-dropdown">
            {notifications.length === 0 ? (
              <p>No notifications</p>
            ) : (
              notifications.map((notification, index) => (
                <div key={index} className="notification-item">
                  <p>{notification.sender} wants to connect</p>
                  <button onClick={() => onAccept(notification.sender)}>Accept</button>
                  <button onClick={() => onDecline(notification.sender)}>Decline</button>
                </div>
              ))
            )}
          </div>
        </div>
      </nav>
    </header>
  );
}
