import React, { useEffect, useState } from 'react';
import { useRoutes, Navigate } from 'react-router-dom';  // Correct imports
import HomePage from './pages/Home';
import Auth from './pages/loginsignup/auth';
import FileShare from './FileShare/FileShare';
import UserPrompt from './FileShare/UserPrompt';
import { socket } from './socket';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (status) => {
    setIsLoggedIn(status);
  };

  // Define your routes based on the login status
  const routes = [
    { path: '/', element: <Auth onLogin={handleLogin} /> },
    { path: '/home', element: isLoggedIn ? <HomePage /> : <Navigate to="/" /> },
    { path: '/user-prompt', element: isLoggedIn ? <UserPrompt /> : <Navigate to="/" /> },
    { path: '/file-share', element: isLoggedIn ? <FileShare /> : <Navigate to="/" /> },
  ];

  const element = useRoutes(routes);

  useEffect(() => {
    if (!socket.connected) {
      socket.connect();
    }

    socket.on('connect', () => {
      console.log('Socket connected:', socket.id);
    });

    socket.on('disconnect', () => {
      console.log('Socket disconnected');
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
    };
  }, []);

  return element; // Return the routes element without wrapping in Router
};

export default App;
