import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";

const SenderApp = () => {
  const [joinID, setJoinID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [fileTransfer, setFileTransfer] = useState({
    file: null,
    buffer: null,
    progress: 0,
  });

  const socket = useRef(null);
  const joinIDRef = useRef("");

  const CHUNK_SIZE = 1024; // Predefined chunk size

  useEffect(() => {
    socket.current = io("http://localhost:5000");

    socket.current.on("init", (uid) => {
      console.log(`Receiver connected with ID: ${uid}`);
      setActiveScreen("fs-screen");
    });

    socket.current.on("fs-share", () => {
      console.log("Receiver requested file chunk. Sending next chunk...");
      sendNextChunk();
    });

    return () => {
      socket.current.disconnect();
    };
  }, []);

  const generateID = () => `${Math.trunc(Math.random() * 999)}-${Math.trunc(Math.random() * 999)}-${Math.trunc(Math.random() * 999)}`;

  const handleCreateRoom = () => {
    const newJoinID = generateID();
    setJoinID(newJoinID);
    joinIDRef.current = newJoinID;
    socket.current.emit("sender-join", { uid: newJoinID });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      const buffer = new Uint8Array(reader.result);
      setFileTransfer({ file, buffer, progress: 0 });

      socket.current.emit("file-meta", {
        uid: joinIDRef.current,
        metadata: {
          filename: file.name,
          total_buffer_size: buffer.length,
        },
      });
    };
    reader.readAsArrayBuffer(file);
  };

  const sendNextChunk = () => {
    setFileTransfer((prev) => {
      if (!prev.buffer || prev.buffer.length === 0) return prev;

      const chunk = prev.buffer.slice(0, CHUNK_SIZE);
      const remainingBuffer = prev.buffer.slice(CHUNK_SIZE);

      socket.current.emit("file-raw", {
        uid: joinIDRef.current,
        buffer: chunk,
      });

      return {
        ...prev,
        buffer: remainingBuffer,
        progress: Math.trunc(((prev.file.size - remainingBuffer.length) / prev.file.size) * 100),
      };
    });
  };

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <h2>Create a Room</h2>
          <button onClick={handleCreateRoom}>Create Room</button>
          {joinID && <p>Room ID: {joinID}</p>}
        </div>
      )}
      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <h2>Share a File</h2>
          <input type="file" onChange={handleFileChange} />
          {fileTransfer.file && (
            <div>
              <p>{fileTransfer.file.name}</p>
              <p>Progress: {fileTransfer.progress}%</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SenderApp;
