    if (!prev.metadata) {
      console.error("Metadata is missing!");
      return prev;
    }

    const newBuffer = new Uint8Array(prev.buffer); // Create a copy of the existing buffer

    // Ensure no out-of-bounds write
    const end = prev.transmitted + buffer.byteLength;
    if (end > newBuffer.length) {
      console.error("Buffer overflow detected!");
      return prev; // Avoid updating state to prevent breaking the app
    }

    // Write the chunk to the buffer
    newBuffer.set(new Uint8Array(buffer), prev.transmitted);

    const transmitted = prev.transmitted + buffer.byteLength;
    const progress = Math.trunc(
      (transmitted / prev.metadata.total_buffer_size) * 100
    );

    if (transmitted >= prev.metadata.total_buffer_size) {
      console.log("File received completely. Downloading...");
      const blob = new Blob([newBuffer]);
      download(blob, prev.metadata.filename);
      return { metadata: null, transmitted: 0, buffer: new Uint8Array(0), progress: 0 };
    }

    return {
      ...prev,
      transmitted,
      buffer: newBuffer,
      progress,
    };
  });

  // Request the next chunk
  socket.current.emit("fs-start", { uid: roomID });
