import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const App = () => {
  const [joinID, setJoinID] = useState("");
  const [receiverID, setReceiverID] = useState(null);
  const [sharedFiles, setSharedFiles] = useState([]);
  const [activeScreen, setActiveScreen] = useState("join-screen");

  const socket = io("http://localhost:5000");

  useEffect(() => {
    const handleFileTransfer = () => {
      const activeFile = sharedFiles[0]; // Assume the first file in the list is being shared
      if (activeFile && activeFile.buffer) {
        let { buffer, metadata } = activeFile;

        let chunk = buffer.slice(0, metadata.buffer_size);
        buffer = buffer.slice(metadata.buffer_size, buffer.length);

        const progress = Math.trunc(
          ((metadata.total_buffer_size - buffer.length) /
            metadata.total_buffer_size) *
            100
        );
        setSharedFiles((prev) =>
          prev.map((f) =>
            f.filename === activeFile.filename
              ? { ...f, progress, buffer }
              : f
          )
        );

        if (chunk.length !== 0) {
          socket.emit("file-raw", { uid: receiverID, buffer: chunk });
        }
      }
    };

    socket.on("init", (uid) => {
      setReceiverID(uid);
      setActiveScreen("fs-screen");
    });

    socket.on("fs-share", handleFileTransfer);

    return () => {
      socket.off("init");
      socket.off("fs-share");
    };
  }, [sharedFiles, receiverID]);

  const generateID = () => {
    return `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
  };

  const handleCreateRoom = () => {
    const id = generateID();
    setJoinID(id);
    socket.emit("sender-join", { uid: id });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
  
    const reader = new FileReader();
    reader.onload = (e) => {
      const buffer = new Uint8Array(reader.result);
      const fileMeta = {
        filename: file.name,
        total_buffer_size: buffer.length,
        buffer_size: 1024, // size of each chunk
      };
  
      const newFile = {
        filename: file.name,
        progress: 0,
        metadata: fileMeta,
        buffer,
      };
  
      setSharedFiles((prev) => [...prev, newFile]);
      socket.emit("file-meta", { uid: receiverID, metadata: fileMeta });
  
      // Begin sending chunks
      shareFile(fileMeta, buffer, newFile);
    };
    reader.readAsArrayBuffer(file);
  };
  
  const shareFile = (metadata, buffer, file) => {
    let offset = 0; // To track where we are in the buffer
  
    const sendNextChunk = () => {
      const chunk = buffer.slice(offset, offset + metadata.buffer_size);
      offset += chunk.length;
  
      const progress = Math.trunc(
        ((metadata.total_buffer_size - buffer.length) / metadata.total_buffer_size) * 100
      );
      setSharedFiles((prev) =>
        prev.map((f) =>
          f.filename === file.filename ? { ...f, progress } : f
        )
      );
  
      if (chunk.length > 0) {
        socket.emit("file-raw", { uid: receiverID, buffer: chunk });
      } else {
        console.log('File transfer complete!');
      }
    };
  
    socket.on("fs-share", sendNextChunk);
    sendNextChunk(); // Start sending the first chunk
  };
  

  return (
    <div className="app">
      {/* Join Screen */}
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <div className="form">
            <h2>Share your files Securely</h2>
            <div className="form-input">
              <button id="sender-start-con-btn" onClick={handleCreateRoom}>
                Create room
              </button>
            </div>
            <div className="form-input" id="join-id">
              {joinID && (
                <>
                  <b>Room ID</b>
                  <span>{joinID}</span>
                </>
              )}
            </div>
          </div>
        </div>
      )}

      {/* File Sharing Screen */}
      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <div className="file-input">
            <label htmlFor="file-input">
              Click here to select files for sharing
            </label>
            <input type="file" id="file-input" onChange={handleFileChange} />
          </div>
          <div className="files-list">
            <div className="title">Shared Files</div>
            {sharedFiles.map((file, index) => (
              <div key={index} className="item">
                <div className="progress">{file.progress}%</div>
                <div className="filename">{file.filename}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
