import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import './FileShare.css';

const FileShareApp = () => {
  const [joinID, setJoinID] = useState('');
  const [receiverID, setReceiverID] = useState(null);
  const [activeScreen, setActiveScreen] = useState('join'); // Possible values: 'join' or 'fs'
  const [files, setFiles] = useState([]);
  const socket = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Initialize socket connection
    socket.current = io();

    socket.current.on('init', (uid) => {
      setReceiverID(uid);
      setActiveScreen('fs');
    });

    socket.current.on('fs-share', () => {
      if (files.length > 0) {
        const fileData = files[0];
        shareFile(fileData.metadata, fileData.buffer, fileData.progressNode);
      }
    });

    return () => {
      socket.current.disconnect();
    };
  }, [files]);

  const generateID = () => {
    return `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
  };

  const handleCreateRoom = () => {
    const newJoinID = generateID();
    setJoinID(newJoinID);
    socket.current.emit('sender-join', { uid: newJoinID });
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const buffer = new Uint8Array(reader.result);
      const metadata = {
        filename: file.name,
        total_buffer_size: buffer.length,
        buffer_size: 1024,
      };
      const progressNode = { progress: 0 }; // Placeholder for tracking progress

      setFiles([
        {
          filename: file.name,
          metadata,
          buffer,
          progressNode,
        },
      ]);
    };
    reader.readAsArrayBuffer(file);
  };

  const shareFile = (metadata, buffer, progressNode) => {
    socket.current.emit('file-meta', {
      uid: receiverID,
      metadata,
    });

    socket.current.on('fs-share', () => {
      const chunk = buffer.slice(0, metadata.buffer_size);
      buffer = buffer.slice(metadata.buffer_size);
      progressNode.progress = Math.trunc(
        ((metadata.total_buffer_size - buffer.length) / metadata.total_buffer_size) * 100
      );
      if (chunk.length !== 0) {
        socket.current.emit('file-raw', {
          uid: receiverID,
          buffer: chunk,
        });
      }
    });
  };

  const handleJoinRoom = () => {
    navigate('/receiver'); // Navigate to the Receiver page
  };

  return (
    <div className="app">
      {activeScreen === 'join' && (
        <div className="screen join-screen active">
          <div className="form">
            <h2>Share your files Securely</h2>
            <div className="form-input">
              <button id="sender-start-con-btn" onClick={handleCreateRoom}>
                Create room
              </button>
            </div>
            {joinID && (
              <div className="form-input" id="join-id">
                <b>Room ID:</b> <span>{joinID}</span>
              </div>
            )}
            <div className="form-input">
              <button id="join-room-btn" onClick={handleJoinRoom}>
                Join room
              </button>
            </div>
          </div>
        </div>
      )}

      {activeScreen === 'fs' && (
        <div className="screen fs-screen">
          <div className="file-input">
            <label htmlFor="file-input">Click here to select files for sharing</label>
            <input type="file" id="file-input" onChange={handleFileChange} />
          </div>
          <div className="files-list">
            <div className="title">Shared Files</div>
            {files.map((file, index) => (
              <div key={index} className="item">
                <div className="progress">{file.progressNode.progress}%</div>
                <div className="filename">{file.filename}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default FileShareApp;



