import React from 'react';
import { Routes, Route } from 'react-router-dom';  // Import Routes and Route from react-router-dom
import HomePage from './pages/Home'; // Your Home page
import FileShare from './FileShare/FileShare'; // Your FileShare page
import Auth from './pages/loginsignup/auth'; // Authentication page

function App() {
  return (
    <div className="App">
      <Routes>  {/* Use Routes to define your routes */}
        <Route path="/" element={<HomePage />} />
        <Route path="/file-share" element={<FileShare />} />
        <Route path="/auth" element={<Auth />} /> {/* Optional: Authentication */}
      </Routes>
    </div>
  );
}

export default App;
