import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";
import download from "downloadjs";

const ReceiverApp = () => {
  const [roomID, setRoomID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [fileShare, setFileShare] = useState({
    metadata: null,
    progress: 0,
  });

  const socket = useRef(null);
  const fileBufferRef = useRef(null);
  const transmittedRef = useRef(0);
  const fileMetadataRef = useRef(null);

  useEffect(() => {
    socket.current = io("http://localhost:5000");

    socket.current.on("file-meta", (metadata) => {
      console.log(`Metadata received: ${metadata.filename}, size: ${metadata.total_buffer_size}`);
    
      if (!metadata || !metadata.total_buffer_size || !metadata.filename) {
        console.error("Invalid metadata received!");
        return;
      }
    
      fileMetadataRef.current = metadata; // Store metadata
      fileBufferRef.current = new Uint8Array(metadata.total_buffer_size); // Initialize buffer
      transmittedRef.current = 0; // Reset transmitted size
    
      setFileShare({
        metadata,
        transmitted: 0,
        progress: 0,
      });
    
      // Request the first chunk
      socket.current.emit("fs-start", { uid: roomID });
    });
    

    socket.current.on("file-raw", ({ buffer }) => {
      if (!fileBufferRef.current || !fileMetadataRef.current) {
        console.error("Buffer or metadata not initialized!");
        return;
      }
    
      const start = transmittedRef.current;
      const end = start + buffer.byteLength;
    
      if (end > fileMetadataRef.current.total_buffer_size) {
        console.error(
          `Buffer overflow! Expected: ${fileMetadataRef.current.total_buffer_size}, Got: ${end}`
        );
        return;
      }
    
      fileBufferRef.current.set(new Uint8Array(buffer), start); // Update the buffer
      transmittedRef.current = end; // Update transmitted size
    
      const progressValue = Math.trunc(
        (transmittedRef.current / fileMetadataRef.current.total_buffer_size) * 100
      );
      setFileShare((prev) => ({
        ...prev,
        transmitted: transmittedRef.current,
        progress: progressValue,
      }));
    
      if (transmittedRef.current >= fileMetadataRef.current.total_buffer_size) {
        console.log("File transfer complete. Downloading...");
    
        const blob = new Blob([fileBufferRef.current]);
        download(blob, fileMetadataRef.current.filename);
    
        // Reset references after download
        transmittedRef.current = 0;
        fileBufferRef.current = null;
        fileMetadataRef.current = null;
      } else {
        // Request the next chunk
        socket.current.emit("fs-start", { uid: roomID });
      }
    });
    

    return () => {
      socket.current.disconnect();
    };
  }, [roomID]);

  const handleConnect = () => {
    if (!roomID.trim()) return;
    socket.current.emit("receiver-join", { uid: roomID });
    setActiveScreen("fs-screen");
  };

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <h2>Join a Room</h2>
          <input
            type="text"
            placeholder="Room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleConnect}>Join</button>
        </div>
      )}

      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <h2>Receiving File</h2>
          {fileShare.metadata && (
            <div>
              <p>{fileShare.metadata.filename}</p>
              <p>Progress: {fileShare.progress}%</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ReceiverApp;
