import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";

const SenderApp = () => {
  const [joinID, setJoinID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [fileTransfer, setFileTransfer] = useState({
    file: null,
    progress: 0,
  });

  const socket = useRef(null);
  const joinIDRef = useRef("");
  const fileBufferRef = useRef(null);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    socket.current = io("http://localhost:5000");

    socket.current.on("init", (uid) => {
      console.log(`Receiver connected with ID: ${uid}`);
      setActiveScreen("fs-screen");
    });

    socket.current.on("fs-share", () => {
      console.log("Receiver requested file chunk. Sending next chunk...");
      sendNextChunk();
    });

    return () => {
      socket.current.disconnect();
    };
  }, []);

  const generateID = () => {
    return `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
  };

  const handleCreateRoom = () => {
    const newJoinID = generateID();
    setJoinID(newJoinID);
    joinIDRef.current = newJoinID;
    socket.current.emit("sender-join", { uid: newJoinID });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) {
      console.error("No file selected!");
      return;
    }
  
    const reader = new FileReader();
    reader.onload = () => {
      const buffer = new Uint8Array(reader.result); // Convert file data to a buffer
      fileBufferRef.current = buffer; // Initialize the buffer reference
      setFileTransfer({
        file,
        progress: 0,
      });
  
      // Emit metadata to the receiver
      socket.current.emit("file-meta", {
        uid: joinIDRef.current,
        metadata: {
          filename: file.name,
          total_buffer_size: buffer.length,
          buffer_size: 1024, // Size of chunks
        },
      });
  
      console.log(`File metadata sent for: ${file.name}`);
    };
  
    reader.readAsArrayBuffer(file); // Read the file as an ArrayBuffer
  };
  
  

  const sendNextChunk = () => {
    if (!fileBufferRef.current || !fileTransfer.file) {
      console.error("File buffer or file metadata is not available.");
      return;
    }
  
    const chunkSize = 1024;
    const fileBuffer = fileBufferRef.current;
  
    if (fileBuffer.byteLength === 0) {
      console.warn("All chunks have been sent. No data left to send.");
      return;
    }
  
    const chunk = fileBuffer.slice(0, chunkSize);
    fileBufferRef.current = fileBuffer.slice(chunkSize); // Update the buffer
  
    socket.current.emit("file-raw", {
      uid: joinIDRef.current,
      buffer: chunk,
    });
  
    const remainingSize = fileBufferRef.current.byteLength;
    const progressValue = Math.trunc(
      ((fileTransfer.file.size - remainingSize) / fileTransfer.file.size) * 100
    );
    setProgress(progressValue);
  
    console.log(
      `Chunk sent. Progress: ${progressValue}%. Remaining size: ${remainingSize}`
    );
  };
  

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <h2>Create a Room</h2>
          <button onClick={handleCreateRoom}>Create Room</button>
          {joinID && <p>Room ID: {joinID}</p>}
        </div>
      )}

      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <h2>Share a File</h2>
          <input type="file" onChange={handleFileChange} />
          {fileTransfer.file && (
            <div>
              <p>{fileTransfer.file.name}</p>
              <p>Progress: {progress}%</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SenderApp;
