import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";
import download from "downloadjs";

const ReceiverApp = () => {
  const [roomID, setRoomID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [file, setFile] = useState(null);
  const bufferRef = useRef(null);
  const transmittedRef = useRef(0);

  const socket = useRef(null);

  useEffect(() => {
    socket.current = io("http://localhost:5000");

    socket.current.on("file-meta", (metadata) => {
      bufferRef.current = new Uint8Array(metadata.total_buffer_size);
      transmittedRef.current = 0;

      setFile({
        name: metadata.filename,
        size: metadata.total_buffer_size,
        progress: 0,
      });

      socket.current.emit("fs-start", { uid: roomID });
    });

    socket.current.on("file-raw", ({ buffer }) => {
      const start = transmittedRef.current;
      const end = start + buffer.byteLength;
      bufferRef.current.set(new Uint8Array(buffer), start);
      transmittedRef.current = end;

      if (file) {
        const progress = Math.trunc((end / file.size) * 100);
        setFile((prev) => ({ ...prev, progress }));
      }

      if (end >= file.size) {
        const blob = new Blob([bufferRef.current]);
        download(blob, file.name);
        setFile(null);
      } else {
        socket.current.emit("fs-start", { uid: roomID });
      }
    });

    return () => {
      socket.current.disconnect();
    };
  }, [roomID]);

  const handleConnect = () => {
    if (!roomID.trim()) return;
    socket.current.emit("receiver-join", { uid: roomID });
    setActiveScreen("fs-screen");
  };

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div>
          <h2>Join a Room</h2>
          <input
            type="text"
            placeholder="Room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleConnect}>Join</button>
        </div>
      )}

      {activeScreen === "fs-screen" && (
        <div>
          <h2>Receiving File</h2>
          {file && (
            <div>
              <p>{file.name}</p>
              <p>Progress: {file.progress || 0}%</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ReceiverApp;
