import React, { useState, useRef, useEffect } from "react";
import { io } from "socket.io-client";
import download from "downloadjs";

const ReceiverApp = () => {
  const [senderID, setSenderID] = useState("");
  const [activeScreen, setActiveScreen] = useState("join-screen");
  const [fileShare, setFileShare] = useState({
    metadata: null,
    transmitted: 0,
    buffer: [],
    progress: 0,
  });

  const socket = useRef(null);

  useEffect(() => {
    socket.current = io("http://localhost:5000");

    socket.current.on("file-meta", (metadata) => {
      console.log(`Room created with ID: ${newJoinID}`);
      console.log("Metadata received:", metadata);
      setFileShare({
        metadata,
        transmitted: 0,
        buffer: [],
        progress: 0,
      });
      console.log(`Room created with ID: ${newJoinID}`);
      socket.current.emit("fs-start", { uid: senderID });
    });

    socket.current.on("file-raw", ({ buffer }) => {
      console.log(`Chunk received, size: ${buffer.byteLength}`);
      setFileShare((prev) => {
        const transmitted = prev.transmitted + buffer.byteLength;
        const progress = Math.trunc((transmitted / prev.metadata.total_buffer_size) * 100);
    
        if (transmitted >= prev.metadata.total_buffer_size) {
          console.log("All chunks received; assembling file.");
          const blob = new Blob([...prev.buffer, buffer]);
          download(blob, prev.metadata.filename);
          return { metadata: null, transmitted: 0, buffer: [], progress: 0 };
        }
    
        return {
          ...prev,
          transmitted,
          buffer: [...prev.buffer, buffer], // Append new chunk
          progress,
        };
      });
    });
            

    return () => {
      socket.current.disconnect();
    };
  }, [senderID]);

  const handleConnect = () => {
    if (!senderID.trim()) return;
    const joinID = `${Math.trunc(Math.random() * 999)}-${Math.trunc(
      Math.random() * 999
    )}-${Math.trunc(Math.random() * 999)}`;
    console.log(`Room created with ID: ${newJoinID}`);
    socket.current.emit("receiver-join", { uid: joinID, sender_uid: senderID });
    setActiveScreen("fs-screen");
  };

  return (
    <div className="app">
      {activeScreen === "join-screen" && (
        <div className="screen join-screen active">
          <h2>Join a Room</h2>
          <input
            type="text"
            placeholder="Sender ID"
            value={senderID}
            onChange={(e) => setSenderID(e.target.value)}
          />
          <button onClick={handleConnect}>Join</button>
        </div>
      )}

      {activeScreen === "fs-screen" && (
        <div className="screen fs-screen">
          <h2>Receiving File</h2>
          {fileShare.metadata && (
            <div>
              <p>{fileShare.metadata.filename}</p>
              <p>Progress: {fileShare.progress}%</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ReceiverApp;
